// Extract internal articles for linking
const response = $input.all()[0].json;
const prev = $node['Initialize Variables'].json;

// Response is an array from Supabase GET request
const articles = Array.isArray(response) ? response : [];

const internalLinks = articles.slice(0, 8).map(a => ({
  title: a.title || '',
  slug: a.slug || '',
  excerpt: a.excerpt || ''
}));

return {
  json: {
    ...prev,
    internalArticles: internalLinks,
    internalLinksText: internalLinks.length > 0 
      ? internalLinks.map(a => `- [${a.title}](/articles/${a.slug})`).join('\n')
      : 'Aucun article interne disponible'
  }
};

// Build Research request body
const data = $input.all()[0].json;

return {
  json: {
    ...data,
    apiBody: JSON.stringify({
      model: "claude-sonnet-4-5-20250929",
      max_tokens: 2000,
      messages: [{
        role: "user",
        content: `Recherche approfondie sur: ${data.topic}\n\nFournis 10 points clés détaillés en français pour un article optimisé SEO/GEO. Inclus des données factuelles, statistiques 2024-2025, et insights pertinents.`
      }]
    })
  }
};

// Extract research
const response = $input.all()[0].json;
const prev = $node['Extract Internal Links'].json;
const research = response.content?.[0]?.text || '';

return {
  json: {
    ...prev,
    research: research
  }
};

// Build GPT-5.1 Draft request
const data = $input.all()[0].json;

const systemPrompt = `Tu es un expert en rédaction SEO/GEO. Tu génères UNIQUEMENT du JSON valide strictement formaté.\n\nIMPORTANT: Le JSON doit contenir une clé 'html' avec le contenu HTML complet de l'article.\n\nRègle: Le HTML doit être échappé correctement pour JSON.`;

const userPrompt = `Rédige un article complet optimisé SEO/GEO de 2500+ mots sur: ${data.topic}\n\nPlan suggéré:\n${data.outline}\n\nRecherche disponible:\n${data.research}\n\nArticles internes pour liens (intègre 3-5 liens contextuels):\n${data.internalLinksText}\n\nRéponds UNIQUEMENT en JSON avec cette structure:\n{\n  \"html\": \"<article><h1>...</h1><p>...</p></article>\"\n}\n\nCritères:\n- Structure H1 > H2 > H3\n- Paragraphes riches (150+ mots)\n- Listes, exemples concrets\n- Intègre 3-5 liens internes contextuels\n- Ton professionnel et engageant`;

return {
  json: {
    ...data,
    apiBody: JSON.stringify({
      model: "gpt-5-pro-preview",
      input: [{
        role: "user",
        content: [{
          type: "input_text",
          text: `${systemPrompt}\n\n${userPrompt}`
        }]
      }],
      reasoning: { effort: "low" },
      max_output_tokens: 8000,
      text: {
        format: {
          type: "json_schema",
          name: "article_response",
          schema: {
            type: "object",
            properties: {
              html: { type: "string" }
            },
            required: ["html"],
            additionalProperties: false
          },
          strict: true
        }
      }
    })
  }
};

// Extract draft HTML
const response = $input.all()[0].json;
const prev = $node['Extract Research'].json;

let articleHTML = '';

try {
  // GPT-5 Responses API format
  const outputText = response.output?.[0]?.content?.[0]?.text || response.choices?.[0]?.message?.content || '{}';
  const parsed = JSON.parse(outputText);
  articleHTML = parsed.html || '';
} catch (e) {
  articleHTML = '<p>Erreur parsing JSON</p>';
}

return {
  json: {
    ...prev,
    articleHTML: articleHTML,
    draftVersion: 1
  }
};

// Build Review request
const data = $input.all()[0].json;

return {
  json: {
    ...data,
    apiBody: JSON.stringify({
      model: "claude-sonnet-4-5-20250929",
      max_tokens: 3000,
      messages: [{
        role: "user",
        content: `Tu es expert SEO/GEO. Review cet article et améliore-le pour atteindre 95%+ score.

ARTICLE ACTUEL:
${data.articleHTML}

Critères d'amélioration:
- Structure H1>H2>H3 optimale
- Densité mots-clés 2-3%
- Liens internes contextuels
- Paragraphes riches
- Call-to-actions

Réponds en JSON:
{
  "improvedHTML": "<article>...</article>",
  "notes": ["amélioration 1", "amélioration 2"]
}`
      }]
    })
  }
};

// Extract review
const response = $input.all()[0].json;
const prev = $node['Extract Draft'].json;

const reviewText = response.content?.[0]?.text || '{}';
let review = { improvedHTML: '', notes: [] };

try {
  review = JSON.parse(reviewText);
} catch (e) {
  const match = reviewText.match(/```json\\s*([\\s\\S]*?)\\s*```/);
  if (match) {
    try { review = JSON.parse(match[1]); } catch {}
  } else {
    review = { improvedHTML: reviewText, notes: [] };
  }
}

return {
  json: {
    ...prev,
    articleHTML: review.improvedHTML || prev.articleHTML,
    reviewNotes: review.notes || []
  }
};

// Build Score request (Perplexity)
const data = $input.all()[0].json;

const scorePrompt = `Tu es un expert scoring SEO/GEO. Évalue cet article sur 100 points.\n\nARTICLE:\n${data.articleHTML.substring(0, 3000)}...\n\nCritères d'évaluation:\n1. Structure (H1-H6, hiérarchie)\n2. Contenu (richesse, pertinence, longueur)\n3. SEO (mots-clés, meta, liens)\n4. GEO (localisation, intent)\n5. Engagement (CTA, listes, exemples)\n\nRéponds en JSON:\n{\n  \"scores\": {\"seo\": 0-100, \"geo\": 0-100},\n  \"breakdown\": {\"structure\": 0-100, \"content\": 0-100, \"keywords\": 0-100, \"links\": 0-100, \"engagement\": 0-100},\n  \"strengths\": [\"...\"],\n  \"weaknesses\": [\"...\"],\n  \"fixes\": [\"...\"]\n}`;

return {
  json: {
    ...data,
    apiBody: JSON.stringify({
      model: "sonar",
      messages: [{
        role: "system",
        content: "Tu es un expert scoring SEO/GEO. Réponds UNIQUEMENT en JSON valide."
      }, {
        role: "user",
        content: scorePrompt
      }],
      temperature: 0.2,
      max_tokens: 1500
    })
  }
};

// Extract score and decide next step
const response = $input.all()[0].json;
const prev = $node['Extract Review'].json;

let scoreData = { scores: { seo: 0, geo: 0 }, breakdown: {}, strengths: [], weaknesses: [], fixes: [] };

try {
  const scoreText = response.choices?.[0]?.message?.content || '{}';
  scoreData = JSON.parse(scoreText);
} catch (e) {
  const match = (response.choices?.[0]?.message?.content || '').match(/```json\\s*([\\s\\S]*?)\\s*```/);
  if (match) {
    try { scoreData = JSON.parse(match[1]); } catch {}
  }
}

const avgScore = (scoreData.scores.seo + scoreData.scores.geo) / 2;
const iteration = prev.currentIteration + 1;
const shouldRewrite = avgScore < prev.minScore && iteration < prev.maxIterations;

return {
  json: {
    ...prev,
    currentScore: avgScore,
    currentIteration: iteration,
    scoreBreakdown: scoreData,
    shouldRewrite: shouldRewrite,
    status: shouldRewrite ? 'needs_rewrite' : 'ready_to_save'
  }
};

// Build Rewrite request (GPT-5.1)
const data = $input.all()[0].json;

const systemPrompt = `Tu es expert SEO/GEO. Réécris l'article pour corriger les faiblesses identifiées. UNIQUEMENT JSON valide.`;

const userPrompt = `Réécris cet article pour atteindre 95%+ SEO/GEO.\n\nARTICLE ACTUEL:\n${data.articleHTML}\n\nSCORES ACTUELS:\nSEO: ${data.scoreBreakdown.scores.seo}/100\nGEO: ${data.scoreBreakdown.scores.geo}/100\n\nFAIBLESSES À CORRIGER:\n${data.scoreBreakdown.weaknesses.join('\\n')}\n\nCORRECTIONS SUGGÉRÉES:\n${data.scoreBreakdown.fixes.join('\\n')}\n\nRéponds en JSON: {\"html\": \"<article>...</article>\"}`;

return {
  json: {
    ...data,
    apiBody: JSON.stringify({
      model: "gpt-5-pro-preview",
      input: [{
        role: "user",
        content: [{
          type: "input_text",
          text: `${systemPrompt}\n\n${userPrompt}`
        }]
      }],
      reasoning: { effort: "medium" },
      max_output_tokens: 8000,
      text: {
        format: {
          type: "json_schema",
          name: "rewrite_response",
          schema: {
            type: "object",
            properties: {
              html: { type: "string" }
            },
            required: ["html"],
            additionalProperties: false
          },
          strict: true
        }
      }
    })
  }
};

// Extract rewrite and loop back
const response = $input.all()[0].json;
const prev = $node['Extract Score & Decide'].json;

let rewrittenHTML = '';

try {
  const outputText = response.output?.[0]?.content?.[0]?.text || '{}';
  const parsed = JSON.parse(outputText);
  rewrittenHTML = parsed.html || '';
} catch (e) {
  rewrittenHTML = prev.articleHTML;
}

return {
  json: {
    ...prev,
    articleHTML: rewrittenHTML,
    shouldRewrite: false // Force exit to re-score
  }
};
