// Parse review response
const response = $input.item.json;
const reviewText = response.content?.[0]?.text || '{}';
let review;

try {
  review = JSON.parse(reviewText);
} catch (e) {
  // Extract JSON from markdown code blocks
  const match = reviewText.match(/```json\s*([\s\S]*?)\s*```/);
  if (match) {
    try {
      review = JSON.parse(match[1]);
    } catch (e2) {
      review = { improved_html: reviewText, notes: ['Parse error'] };
    }
  } else {
    review = { improved_html: reviewText, notes: [] };
  }
}

return {
  jobId: $node['Save to Supabase'].json.jobId || $node['Process Introduction'].json.jobId,
  improved_html: review.improved_html || '',
  notes: review.notes || [],
  step: 'review_complete'
};
