// Build Perplexity Score request
const articleHTML = $json.articleHTML;

const scorePrompt = `Tu es un expert scoring SEO/GEO. Évalue cet article sur 100 points.

ARTICLE:
${articleHTML}

Critères d'évaluation:
1. Structure (H1-H6, hiérarchie)
2. Contenu (richesse, pertinence, longueur)
3. SEO (mots-clés, meta, liens)
4. GEO (localisation, intent)
5. Engagement (CTA, listes, exemples)

Réponds en JSON:
{
  "scores": {"seo": 0-100, "geo": 0-100},
  "breakdown": {"structure": 0-100, "content": 0-100, "keywords": 0-100, "links": 0-100, "engagement": 0-100},
  "strengths": ["..."],
  "weaknesses": ["..."],
  "fixes": ["..."]
}`;

return {
  json: {
    apiBody: JSON.stringify({
      model: "sonar",
      messages: [
        {
          role: "system",
          content: "Tu es un expert scoring SEO/GEO. Réponds UNIQUEMENT en JSON valide."
        },
        {
          role: "user",
          content: scorePrompt
        }
      ],
      temperature: 0.2,
      max_tokens: 1500
    })
  }
};

// Extract and display score
const response = $input.all()[0].json;

let scoreData = { scores: { seo: 0, geo: 0 }, breakdown: {}, strengths: [], weaknesses: [], fixes: [] };

try {
  const scoreText = response.choices?.[0]?.message?.content || '{}';
  scoreData = JSON.parse(scoreText);
} catch (e) {
  const match = (response.choices?.[0]?.message?.content || '').match(/```json\s*([\s\S]*?)\s*```/);
  if (match) {
    try { scoreData = JSON.parse(match[1]); } catch {}
  }
}

const avgScore = (scoreData.scores.seo + scoreData.scores.geo) / 2;

return {
  json: {
    success: true,
    avgScore: avgScore,
    seoScore: scoreData.scores.seo,
    geoScore: scoreData.scores.geo,
    breakdown: scoreData.breakdown,
    strengths: scoreData.strengths,
    weaknesses: scoreData.weaknesses,
    fixes: scoreData.fixes,
    rawResponse: response
  }
};
