// Parse Perplexity response and extract topics
const response = $input.first().json;
const content = response.choices[0].message.content;

// Try to extract JSON from the response
let topics;
try {
  // Look for JSON content in the response
  const jsonMatch = content.match(/\{[\s\S]*\}/);
  if (jsonMatch) {
    topics = JSON.parse(jsonMatch[0]);
  } else {
    // Fallback: create topics from text analysis
    topics = {
      topics: [
        {
          title: "Article suggéré par IA",
          keywords: ["intelligence artificielle", "contenu"],
          angle: "Approche pratique et accessible",
          audience: "Professionnels et entreprises",
          sources: ["Documentation officielle", "Études de cas"]
        }
      ]
    };
  }
} catch (error) {
  // Error handling
  topics = {
    topics: [
      {
        title: "Article de contenu par défaut",
        keywords: ["contenu", "marketing"],
        angle: "Guide pratique",
        audience: "Professionnels du marketing",
        sources: ["Recherche web", "Meilleures pratiques"]
      }
    ]
  };
}

// Return each topic as separate item for processing
const results = [];
topics.topics.forEach((topic, index) => {
  results.push({
    json: {
      topicIndex: index,
      topicData: topic,
      searchContent: JSON.stringify(topic)
    }
  });
});

return results;

// Combine all results and prepare final output
const items = $input.all();
const results = [];

items.forEach((item, index) => {
  const data = item.json;

  // Extract data from previous nodes
  let topicData, article, review;

  try {
    // Parse topic data
    if (data.topicData) {
      topicData = data.topicData;
    }

    // Parse article from OpenAI response
    if (data.choices && data.choices[0]) {
      const content = data.choices[0].message.content;
      const jsonMatch = content.match(/\{[\s\S]*\}/);
      if (jsonMatch) {
        article = JSON.parse(jsonMatch[0]).article;
      }
    }

    // Parse review from Anthropic response
    if (data.content && data.content[0]) {
      const content = data.content[0].text;
      const jsonMatch = content.match(/\{[\s\S]*\}/);
      if (jsonMatch) {
        review = JSON.parse(jsonMatch[0]).review;
      }
    }

  } catch (error) {
    console.log('Parsing error:', error);
  }

  // Create comprehensive result
  results.push({
    json: {
      timestamp: new Date().toISOString(),
      workflowId: 'content-agents-workflow',
      topic: topicData || { title: 'Topic ' + (index + 1) },
      article: article || { title: 'Generated Article', content: 'Article content' },
      review: review || { globalScore: 0, recommendations: [] },
      status: 'completed'
    }
  });
});

return results;
